InDataTableV2.typeDefs:
import { DefineComponent } from 'vue';

/**
 * Column definition for data table.
 */
export interface TableColumn {
  /**
   * Data property key to display in this column.
   * Maps to property name in row data objects.
   */
  key: string;

  /**
   * Column header label text.
   */
  label: string;

  /**
   * Enable sorting for this column.
   *
   * @default false
   */
  sortable?: boolean;

  /**
   * Column width (CSS value).
   *
   * @example width="120px"
   * @example width="20%"
   */
  width?: string;

  /**
   * Custom cell renderer component name.
   * Uses AG Grid's cell renderer framework.
   *
   * @example cellRenderer="InStatus"
   * @example cellRenderer="InActions"
   */
  cellRenderer?: string;

  /**
   * Parameters to pass to cell renderer.
   *
   * @example
   * cellRendererParams: {
   *   statusMap: {
   *     active: { text: 'Active', type: 'success' }
   *   }
   * }
   */
  cellRendererParams?: any;

  /**
   * Whether column is pinned (fixed) left or right.
   *
   * @example pinned="left"
   * @example pinned="right"
   */
  pinned?: 'left' | 'right';

  /**
   * Whether column is visible.
   *
   * @default true
   */
  hide?: boolean;

  /**
   * Whether column can be resized.
   *
   * @default true
   */
  resizable?: boolean;

  /**
   * Custom filter component for this column.
   */
  filter?: string;

  /**
   * Custom comparator function for sorting.
   */
  comparator?: (valueA: any, valueB: any) => number;
}

/**
 * InDataTableV2 component props.
 */
export interface InDataTableV2Props {
  /**
   * Unique identifier for the data table.
   *
   * @required
   * @example id="users-table"
   * @example id="orders-grid"
   */
  id: string;

  /**
   * Column definitions for the table.
   * Defines structure, headers, and cell renderers.
   *
   * @required
   * @example
   * :columns="[
   *   { key: 'name', label: 'Name', sortable: true },
   *   { key: 'email', label: 'Email', sortable: true },
   *   { key: 'role', label: 'Role', width: '120px' }
   * ]"
   */
  columns: TableColumn[];

  /**
   * Array of row data objects.
   * Each object represents one row in the table.
   *
   * @required
   * @example
   * :data="[
   *   { name: 'John Doe', email: 'john@example.com', role: 'Admin' },
   *   { name: 'Jane Smith', email: 'jane@example.com', role: 'User' }
   * ]"
   */
  data: any[];

  /**
   * Shows loading state with spinner or skeleton.
   *
   * @default false
   * @example :loading="isLoadingData"
   */
  loading?: boolean;

  /**
   * Enables pagination controls.
   *
   * @default false
   * @example :pagination="true"
   */
  pagination?: boolean;

  /**
   * Enables sorting globally.
   * Individual columns can override with sortable prop.
   *
   * @default false
   * @example :sortable="true"
   */
  sortable?: boolean;

  /**
   * Page size for pagination.
   *
   * @default 10
   * @example :page-size="25"
   */
  pageSize?: number;

  /**
   * Enables row selection (checkboxes).
   *
   * @default false
   * @example :row-selection="true"
   */
  rowSelection?: boolean;

  /**
   * Row selection mode.
   *
   * @default 'multiple'
   * @example row-selection-mode="single"
   */
  rowSelectionMode?: 'single' | 'multiple';

  /**
   * Custom grid options for AG Grid.
   * Allows full customization of AG Grid behavior.
   *
   * @example
   * :grid-options="{
   *   animateRows: true,
   *   enableCellTextSelection: true
   * }"
   */
  gridOptions?: any;
}

/**
 * InDataTableV2 Component
 *
 * Enterprise-grade data table built on AG Grid with sorting, filtering,
 * pagination, bulk actions, and custom cell renderers.
 *
 * @example
 * // Basic usage
 * <InDataTableV2
 *   id="users"
 *   :columns="[
 *     { key: 'name', label: 'Name', sortable: true },
 *     { key: 'email', label: 'Email' }
 *   ]"
 *   :data="users"
 *   :pagination="true"
 * />
 *
 * @example
 * // With custom cell renderer
 * <InDataTableV2
 *   id="orders"
 *   :columns="[
 *     { key: 'orderNumber', label: 'Order #' },
 *     {
 *       key: 'status',
 *       label: 'Status',
 *       cellRenderer: 'InStatus',
 *       cellRendererParams: {
 *         statusMap: {
 *           pending: { text: 'Pending', type: 'warning' },
 *           completed: { text: 'Done', type: 'success' }
 *         }
 *       }
 *     }
 *   ]"
 *   :data="orders"
 * />
 *
 * @example
 * // With row selection
 * <InDataTableV2
 *   id="emails"
 *   :columns="columns"
 *   :data="emails"
 *   :row-selection="true"
 *   row-selection-mode="multiple"
 *   @row-selected="handleSelection"
 * />
 */
declare const InDataTableV2: DefineComponent<InDataTableV2Props>;

export default InDataTableV2;

