    scriptSetup: `import { ref } from 'vue';

const dateState = ref<'default' | 'error' | 'warning'>('default');
const stateMessage = ref('');

const handleEventDate = (date: string) => {
  // Validate the selected date
  const selectedDate = new Date(date);
  const today = new Date();

  if (selectedDate < today) {
    dateState.value = 'error';
    stateMessage.value = 'Event date must be in the future';
  } else if (selectedDate.getDay() === 0 || selectedDate.getDay() === 6) {
    dateState.value = 'warning';
    stateMessage.value = 'Events on weekends may have limited availability';
  } else {
    dateState.value = 'default';
    stateMessage.value = '';
    console.log('Valid event date:', date);
  }
};`,
