InDatePickerV2.typeDefs:
import { DefineComponent } from 'vue';

/**
 * Theme variants for the date picker.
 */
export type DatePickerTheme = 'grey' | 'white';

/**
 * State variants for validation and feedback.
 */
export type DatePickerState = 'default' | 'error' | 'warning';

/**
 * Calendar popover alignment options.
 */
export type CalendarPopoverAlign = 'left' | 'right' | 'center';

/**
 * Week start day options.
 */
export type WeekStartDay = 'Sunday' | 'Monday';

/**
 * Date range object structure.
 */
export interface DateRange {
  /**
   * Start date of the range in dd.mm.yyyy or mm/dd/yyyy format.
   */
  startDate: string;

  /**
   * End date of the range in dd.mm.yyyy or mm/dd/yyyy format.
   */
  endDate: string;
}

/**
 * Disabled date range configuration.
 */
export interface DisabledRange {
  /**
   * Start date of disabled range.
   */
  startDate: string;

  /**
   * End date of disabled range.
   */
  endDate: string;
}

/**
 * Skeleton loader sizing configuration.
 */
export interface SkeletonSizing {
  /**
   * Skeleton width in pixels.
   */
  width: number;

  /**
   * Number of skeleton elements.
   */
  number: number;
}

/**
 * Custom range configuration.
 */
export interface CustomRange {
  /**
   * Unique name for the range.
   */
  name: string;

  /**
   * Display title for the range.
   */
  title: string;

  /**
   * Start date of the range.
   */
  startDate: string;

  /**
   * End date of the range.
   */
  endDate: string;

  /**
   * Whether the range is disabled.
   * @default false
   */
  disabled?: boolean;
}

/**
 * Calendar popover message configuration.
 */
export interface CalendarPopoverMessage {
  /**
   * Message text content.
   */
  text: string;

  /**
   * Message type affecting icon and styling.
   */
  type: 'info' | 'warning' | 'error';
}

/**
 * Specific date highlight configuration.
 */
export interface SpecificDate {
  /**
   * Date to highlight.
   */
  date: string;

  /**
   * Label text for the date popover.
   */
  label: string;

  /**
   * Color for the date indicator dot.
   */
  color: string;

  /**
   * Whether to show a dot indicator.
   * @default false
   */
  withDot?: boolean;

  /**
   * Hide the popover indicator.
   * @default false
   */
  hideIndicator?: boolean;
}

/**
 * Locale format mapping configuration.
 */
export interface FormatMapping {
  'en-US': string;
  'en-GB': string;
  'tr-TR': string;
  'default': string;
  [key: string]: string;
}

/**
 * Default range names supported by the date picker.
 */
export type DefaultRangeName =
  | 'today'
  | 'yesterday'
  | 'last-week'
  | 'current-month'
  | 'last-month'
  | 'last-3-month'
  | 'current-year'
  | 'last-year';

/**
 * InDatePickerV2 component props.
 */
export interface InDatePickerV2Props {
  /**
   * Unique identifier for the date picker element.
   * Used for DOM manipulation, testing, and accessibility.
   *
   * @required
   * @example 'campaign-date-picker'
   * @example 'report-date-range'
   */
  id: string;

  /**
   * Name attribute for the date picker input.
   * Used for form submission and accessibility.
   *
   * @required
   * @example 'dateRange'
   * @example 'reportPeriod'
   */
  name: string;

  /**
   * Visual theme variant of the date picker.
   *
   * @default 'grey'
   * @example 'grey' // Default grey background
   * @example 'white' // White background variant
   */
  theme?: DatePickerTheme;

  /**
   * Validation state of the date picker.
   * Affects visual appearance and shows state icons.
   *
   * @default 'default'
   * @example 'default' // Normal state
   * @example 'error' // Shows error styling and icon
   * @example 'warning' // Shows warning styling and icon
   */
  state?: DatePickerState;

  /**
   * State message displayed alongside state icon.
   * Provides context for error or warning states.
   *
   * @default ''
   * @example 'Date range is required'
   * @example 'Selected dates exceed limit'
   */
  stateMessage?: string;

  /**
   * Disables the apply button.
   * Prevents users from confirming date selection.
   *
   * @default false
   * @example :disabled-apply-button="!isRangeValid"
   */
  disabledApplyButton?: boolean;

  /**
   * Tooltip text for the apply button.
   * Shown when apply button needs additional context.
   *
   * @default ''
   * @example 'Select both date ranges to continue'
   */
  applyButtonTooltip?: string;

  /**
   * Shows the helper message below the date picker.
   *
   * @default false
   * @example :helper-message-status="true"
   */
  helperMessageStatus?: boolean;

  /**
   * Helper message text content.
   * Provides additional guidance to users.
   *
   * @default ''
   * @example 'Select a date range for your report'
   */
  helperMessage?: string;

  /**
   * Shows the label above the date picker.
   *
   * @default true
   * @example :label-status="false" // Hide label
   */
  labelStatus?: boolean;

  /**
   * Label text content.
   *
   * @default 'Label'
   * @example 'Date Range'
   * @example 'Report Period'
   */
  labelText?: string;

  /**
   * Placeholder text for the date input field.
   *
   * @default ''
   * @example 'Select date range'
   */
  placeholderText?: string;

  /**
   * Current value of the date picker.
   * For single date: string or array with one date.
   * For range: array with date range objects.
   *
   * @default ''
   * @example '1/15/2025' // Single date
   * @example [{ startDate: '1/1/2025', endDate: '1/31/2025' }] // Single range
   * @example [{ startDate: '1/1/2025', endDate: '1/31/2025' }, { startDate: '12/1/2024', endDate: '12/31/2024' }] // Comparison
   */
  value?: string | DateRange[];

  /**
   * Minimum selectable date.
   * Can be a Date object or string in format specified by locale.
   *
   * @default ''
   * @example '1/1/2020'
   * @example new Date('2020-01-01')
   */
  minDate?: string | Date;

  /**
   * Maximum selectable date.
   * Can be a Date object or string in format specified by locale.
   *
   * @default ''
   * @example '12/31/2025'
   * @example new Date('2025-12-31')
   */
  maxDate?: string | Date;

  /**
   * Enables single date selection mode instead of date range.
   *
   * @default false
   * @example :single-date-picker-status="true" // Select single date only
   */
  singleDatePickerStatus?: boolean;

  /**
   * Shows quick range selection buttons (Today, Yesterday, etc.).
   *
   * @default false
   * @example :quick-range-selection-status="true"
   */
  quickRangeSelectionStatus?: boolean;

  /**
   * Enables comparison date range feature.
   * Allows selecting a second date range to compare with the first.
   *
   * @default false
   * @example :comparison-status="true"
   */
  comparisonStatus?: boolean;

  /**
   * Disables the comparison range toggle.
   * Used when comparison is not available for current selection.
   *
   * @default false
   * @example :disabled-comparison-status="dateRangeTooLong"
   */
  disabledComparisonStatus?: boolean;

  /**
   * Tooltip text explaining why comparison is disabled.
   *
   * @default ''
   * @example 'Comparison is not available for date ranges longer than 90 days'
   */
  disabledComparisonTooltipText?: string;

  /**
   * Alignment of the calendar popover relative to the input.
   *
   * @default 'left'
   * @example 'left' // Align to left edge
   * @example 'right' // Align to right edge
   * @example 'center' // Center align
   */
  calendarPopoverAlign?: CalendarPopoverAlign;

  /**
   * Date range to disable (makes dates unselectable).
   *
   * @default { startDate: '', endDate: '' }
   * @example :disabled-range="{ startDate: '1/1/2025', endDate: '1/15/2025' }"
   */
  disabledRange?: DisabledRange;

  /**
   * Array of messages to display in calendar popover.
   * Used for info, warnings, or contextual help.
   *
   * @default []
   * @example
   * :calendar-popover-message="[
   *   { text: 'Data may be incomplete', type: 'warning' }
   * ]"
   */
  calendarPopoverMessage?: CalendarPopoverMessage[];

  /**
   * Array of specific dates to highlight with custom styling.
   * Useful for marking special dates, events, or data points.
   *
   * @default []
   * @example
   * :specific-dates="[
   *   { date: '1/15/2025', label: 'Campaign Launch', color: 'blue', withDot: true }
   * ]"
   */
  specificDates?: SpecificDate[];

  /**
   * Array of custom quick range options.
   * Extends the default range options with custom date ranges.
   *
   * @default []
   * @example
   * :custom-ranges="[
   *   { name: 'q1', title: 'Q1 2025', startDate: '1/1/2025', endDate: '3/31/2025' }
   * ]"
   */
  customRanges?: CustomRange[];

  /**
   * Array of custom comparison range options.
   *
   * @default []
   * @example
   * :compared-custom-ranges="[
   *   { name: 'last-q1', title: 'Q1 2024', startDate: '1/1/2024', endDate: '3/31/2024' }
   * ]"
   */
  comparedCustomRanges?: CustomRange[];

  /**
   * Shows tooltip icon and text next to label.
   *
   * @default false
   * @example :tooltip-status="true"
   */
  tooltipStatus?: boolean;

  /**
   * Tooltip text content.
   *
   * @default ''
   * @example 'Select the date range for your analysis'
   */
  tooltipText?: string;

  /**
   * Shows clear selection button to reset dates.
   *
   * @default false
   * @example :clear-selection-button="true"
   */
  clearSelectionButton?: boolean;

  /**
   * Swaps day and month positions in date format.
   * When true, formats as dd/mm/yyyy instead of mm/dd/yyyy.
   *
   * @default false
   * @example :day-to-month="true" // European format
   */
  dayToMonth?: boolean;

  /**
   * Timezone offset in hours from UTC.
   * Used to adjust dates for different timezones.
   *
   * @default 0
   * @example :time-zone="3" // UTC+3
   * @example :time-zone="-5" // UTC-5
   */
  timeZone?: number;

  /**
   * Disables the entire date picker.
   * Prevents all user interaction.
   *
   * @default false
   * @example :disabled-status="!hasPermission"
   */
  disabledStatus?: boolean;

  /**
   * Shows skeleton loading placeholder.
   * Used during initial page load or data fetching.
   *
   * @default false
   * @example :skeleton-status="isLoading"
   */
  skeletonStatus?: boolean;

  /**
   * Dimensions for the skeleton loader.
   *
   * @default { width: 0, number: 0 }
   * @example :skeleton-sizing="{ width: 300, number: 1 }"
   */
  skeletonSizing?: SkeletonSizing;

  /**
   * Day of the week to start the calendar.
   *
   * @default 'Sunday'
   * @example 'Sunday' // Week starts on Sunday
   * @example 'Monday' // Week starts on Monday
   */
  weekStartDay?: WeekStartDay;

  /**
   * Enables XSS protection for label text.
   * Sanitizes label text to prevent XSS attacks.
   *
   * @default false
   * @example :prevent-xss="true"
   */
  preventXss?: boolean;

  /**
   * Array of default range names to show in quick selection.
   * Filters which default ranges appear in the UI.
   *
   * @default ['today', 'yesterday', 'last-week', 'current-month', 'last-month', 'last-3-month', 'current-year', 'last-year']
   * @example :default-ranges="['today', 'yesterday', 'last-week']"
   */
  defaultRanges?: DefaultRangeName[];

  /**
   * Error message shown for invalid date input.
   *
   * @default 'Invalid Date'
   * @example 'Please enter a valid date'
   */
  invalidDateMessage?: string;

  /**
   * Shows min/max date constraint messages automatically.
   *
   * @default true
   * @example :min-max-date-message-status="false" // Hide auto messages
   */
  minMaxDateMessageStatus?: boolean;

  /**
   * Position of tooltips on period selection buttons.
   *
   * @default 'bottom center'
   * @example 'top center'
   * @example 'bottom left'
   */
  periodTooltipPosition?: string;

  /**
   * Locale for date formatting.
   * Determines date format based on locale conventions.
   *
   * @default 'default'
   * @example 'en-US' // mm/dd/yyyy
   * @example 'en-GB' // dd/mm/yyyy
   * @example 'tr-TR' // dd.mm.yyyy
   */
  locale?: string;

  /**
   * Custom format mapping for different locales.
   * Allows overriding default date formats per locale.
   *
   * @default { 'en-US': 'mm/dd/yyyy', 'en-GB': 'dd/mm/yyyy', 'tr-TR': 'dd.mm.yyyy', 'default': 'dd.mm.yyyy' }
   * @example
   * :format-mapping="{
   *   'en-US': 'mm/dd/yyyy',
   *   'en-GB': 'dd/mm/yyyy',
   *   'custom': 'yyyy-mm-dd'
   * }"
   */
  formatMapping?: FormatMapping;
}

/**
 * Event payload for date range updates.
 */
export interface DateRangeUpdatePayload {
  start: string;
  end: string;
}

/**
 * Event handler types for InDatePickerV2.
 */
export interface InDatePickerV2EventHandlers {
  /**
   * Emitted when user clicks apply button.
   * Returns formatted date string(s) based on selection mode.
   *
   * @param payload - Single date string, single range string, or array with comparison ranges
   * @example
   * // Single date mode
   * (payload: string) => console.log(payload) // '1/15/2025'
   *
   * @example
   * // Range mode
   * (payload: string) => console.log(payload) // '1/1/2025-1/31/2025'
   *
   * @example
   * // Comparison mode
   * (payload: string[]) => console.log(payload) // ['1/1/2025-1/31/2025', '12/1/2024-12/31/2024']
   */
  onApply?: (payload: string | string[]) => void;

  /**
   * Emitted when calendar popover opens.
   *
   * @param payload - Current predefined range
   */
  onOpen?: (payload: DateRangeUpdatePayload) => void;

  /**
   * Emitted when calendar popover closes.
   *
   * @param payload - Current predefined range
   */
  onClose?: (payload: DateRangeUpdatePayload) => void;

  /**
   * Emitted when comparison toggle is enabled.
   *
   * @param payload - Object containing both predefined and compared ranges
   */
  onOpenComparison?: (payload: { predefinedRange: DateRangeUpdatePayload; comparedRange: DateRangeUpdatePayload }) => void;

  /**
   * Emitted when comparison toggle is disabled.
   *
   * @param payload - Object containing both ranges (compared range will be empty)
   */
  onCloseComparison?: (payload: { predefinedRange: DateRangeUpdatePayload; comparedRange: DateRangeUpdatePayload }) => void;

  /**
   * Emitted when date range is updated (on calendar selection).
   *
   * @param payload - Updated predefined range
   */
  onDateRangeUpdated?: (payload: DateRangeUpdatePayload) => void;

  /**
   * Emitted when comparison date range is updated.
   *
   * @param payload - Updated compared range
   */
  onDateComparedRangeUpdated?: (payload: DateRangeUpdatePayload) => void;

  /**
   * Emitted when a quick range period button is clicked.
   *
   * @param payload - Selected predefined range
   */
  onPeriodSelected?: (payload: DateRangeUpdatePayload) => void;

  /**
   * Emitted when a comparison period button is clicked.
   *
   * @param payload - Selected compared range
   */
  onComparisonPeriodSelected?: (payload: DateRangeUpdatePayload) => void;

  /**
   * Emitted when a date range is selected but not yet applied.
   * Fires after each selection in the calendar.
   *
   * @param payload - Same as apply event payload
   */
  onRangeSelected?: (payload: string | string[]) => void;
}

/**
 * InDatePickerV2 emits configuration (for Vue's EmitsOptions).
 */
export type InDatePickerV2Emits = {
  apply: (payload: string | string[]) => true;
  open: (payload: DateRangeUpdatePayload) => true;
  close: (payload: DateRangeUpdatePayload) => true;
  openComparison: (payload: { predefinedRange: DateRangeUpdatePayload; comparedRange: DateRangeUpdatePayload }) => true;
  closeComparison: (payload: { predefinedRange: DateRangeUpdatePayload; comparedRange: DateRangeUpdatePayload }) => true;
  dateRangeUpdated: (payload: DateRangeUpdatePayload) => true;
  dateComparedRangeUpdated: (payload: DateRangeUpdatePayload) => true;
  periodSelected: (payload: DateRangeUpdatePayload) => true;
  comparisonPeriodSelected: (payload: DateRangeUpdatePayload) => true;
  rangeSelected: (payload: string | string[]) => true;
};

/**
 * InDatePickerV2 Component
 *
 * Advanced date picker component with single date, date range, and comparison range modes.
 * Supports quick range selection, custom ranges, date constraints, and internationalization.
 *
 * @example
 * // Basic single date picker
 * <InDatePickerV2
 *   id="birth-date"
 *   name="birthDate"
 *   label-text="Birth Date"
 *   :single-date-picker-status="true"
 *   @apply="handleDateSelect"
 * />
 *
 * @example
 * // Date range picker with quick ranges
 * <InDatePickerV2
 *   id="report-range"
 *   name="reportRange"
 *   label-text="Report Period"
 *   :quick-range-selection-status="true"
 *   :value="[{ startDate: '1/1/2025', endDate: '1/31/2025' }]"
 *   @apply="handleRangeSelect"
 * />
 *
 * @example
 * // Date range with comparison
 * <InDatePickerV2
 *   id="analytics-date"
 *   name="analyticsDate"
 *   label-text="Analytics Period"
 *   :quick-range-selection-status="true"
 *   :comparison-status="true"
 *   :value="[
 *     { startDate: '1/1/2025', endDate: '1/31/2025' },
 *     { startDate: '12/1/2024', endDate: '12/31/2024' }
 *   ]"
 *   @apply="handleComparisonSelect"
 * />
 *
 * @example
 * // With date constraints and custom ranges
 * <InDatePickerV2
 *   id="campaign-date"
 *   name="campaignDate"
 *   label-text="Campaign Period"
 *   :min-date="'1/1/2020'"
 *   :max-date="new Date()"
 *   :quick-range-selection-status="true"
 *   :custom-ranges="[
 *     { name: 'q1', title: 'Q1 2025', startDate: '1/1/2025', endDate: '3/31/2025' }
 *   ]"
 *   @apply="handleCampaignDateSelect"
 * />
 */
declare const InDatePickerV2: DefineComponent<InDatePickerV2Props, {}, {}, {}, {}, {}, {}, InDatePickerV2Emits>;

export default InDatePickerV2;

apply.handlingPattern:
if (Array.isArray(payload)) {
  const [mainRange, comparisonRange] = payload;
  this.$emit('apply-date-selection', mainRange);
  this.$emit('apply-comparison-date-selection', comparisonRange);
} else {
  this.$emit('apply-date-selection', payload);
}

periodSelected.handlingPattern:
handlePeriodSelected(range) {
  const start = this.parseDate(range.start);
  const end = this.parseDate(range.end);
  const periodLength = Math.floor((end - start) / (1000 * 60 * 60 * 24)) + 1;

  // Calculate Previous Period
  const prevEnd = new Date(start);
  prevEnd.setDate(start.getDate() - 1);
  const prevStart = new Date(prevEnd);
  prevStart.setDate(prevEnd.getDate() - periodLength + 1);

  // Previous Period Last Year
  const lastYearStart = new Date(prevStart);
  lastYearStart.setFullYear(prevStart.getFullYear() - 1);
  const lastYearEnd = new Date(prevEnd);
  lastYearEnd.setFullYear(prevEnd.getFullYear() - 1);

  this.comparedCustomRangesForV2 = [{
    name: 'lastYear',
    title: 'Previous Period Last Year',
    startDate: this.formatDateToSlash(lastYearStart),
    endDate: this.formatDateToSlash(lastYearEnd)
  }];
}

helperFunctions.code:
formatDateToSlash(date) {
  const d = new Date(date);
  const month = String(d.getMonth() + 1).padStart(2, '0');
  const day = String(d.getDate()).padStart(2, '0');
  const year = d.getFullYear();
  return `${month}/${day}/${year}`;
}

parseDate(dateStr) {
  const parts = dateStr.split(/[/.]/); 
  const isSlash = dateStr.includes('/');

  if (isSlash) {
    // mm/dd/yyyy
    const [month, day, year] = parts;
    return new Date(parseInt(year), parseInt(month) - 1, parseInt(day));
  } else {
    // dd.mm.yyyy
    const [day, month, year] = parts;
    return new Date(parseInt(year), parseInt(month) - 1, parseInt(day));
  }
}

convertToLocalFormat(dateStr) {
  const [month, day, year] = dateStr.split('/');
  return `${day}.${month}.${year}`;
}

