/**
 * Usage examples for InDropdownMenu
 * These examples are used by:
 * - MCP server for code generation
 * - Documentation site
 * - AI assistants for implementation guidance
 */

export interface ComponentExample {
  id: string;
  title: string;
  description: string;
  category: 'basic' | 'variants' | 'states' | 'icons' | 'groups' | 'forms' | 'advanced';
  complexity: 'beginner' | 'intermediate' | 'advanced';
  code: string;
  props?: Record<string, any>;
  scriptSetup?: string;
  tags: string[];
  notes?: string;
}

export const examples: ComponentExample[] = [
  // ==================== BASIC ====================
  {
    id: 'basic-menu',
    title: 'Basic Dropdown Menu',
    description: 'Simple dropdown with text options',
    category: 'basic',
    complexity: 'beginner',
    code: `<InDropdownMenu
  id="actions-menu"
  :options="options"
  :status="isOpen"
  @select="handleSelect"
/>`,
    props: {
      id: 'actions-menu',
      status: false,
      options: []
    },
    scriptSetup: `const isOpen = ref(false);

const options = [
  { text: 'Edit', value: 'edit' },
  { text: 'Duplicate', value: 'duplicate' },
  { text: 'Delete', value: 'delete' }
];

const handleSelect = (value) => {
  console.log('Selected:', value);
  isOpen.value = false;
};`,
    tags: ['basic', 'menu', 'dropdown']
  },

  {
    id: 'basic-with-icons',
    title: 'Menu with Icons',
    description: 'Dropdown menu items with icons',
    category: 'basic',
    complexity: 'beginner',
    code: `<InDropdownMenu
  id="user-menu"
  :options="options"
  :status="isOpen"
  @select="handleSelect"
/>`,
    props: {
      id: 'user-menu',
      status: false
    },
    scriptSetup: `const isOpen = ref(false);

const options = [
  { text: 'Profile', value: 'profile', icon: 'user' },
  { text: 'Settings', value: 'settings', icon: 'settings' },
  { text: 'Help', value: 'help', icon: 'question-circle' },
  { text: 'Logout', value: 'logout', icon: 'logout' }
];

const handleSelect = (value) => {
  if (value === 'logout') {
    performLogout();
  }
  isOpen.value = false;
};`,
    tags: ['basic', 'icons', 'menu']
  },

  {
    id: 'basic-with-trigger',
    title: 'Menu with Button Trigger',
    description: 'Complete example with button trigger',
    category: 'basic',
    complexity: 'beginner',
    code: `<div class="dropdown-container">
  <InButtonV2
    type="secondary"
    label-text="Actions"
    right-icon="chevron-down"
    @click="toggleMenu"
  />

  <InDropdownMenu
    id="dropdown"
    :options="options"
    :status="isOpen"
    @select="handleSelect"
  />
</div>`,
    props: {
      id: 'dropdown',
      status: false
    },
    scriptSetup: `const isOpen = ref(false);

const options = [
  { text: 'View Details', value: 'view' },
  { text: 'Share', value: 'share' },
  { text: 'Export', value: 'export' }
];

const toggleMenu = () => {
  isOpen.value = !isOpen.value;
};

const handleSelect = (value) => {
  console.log('Action:', value);
  isOpen.value = false;
};`,
    tags: ['basic', 'trigger', 'button']
  },

  // ==================== VARIANTS ====================
  {
    id: 'variant-dividers',
    title: 'Menu with Dividers',
    description: 'Group menu items with separator lines',
    category: 'variants',
    complexity: 'beginner',
    code: `<InDropdownMenu
  id="menu-with-dividers"
