];`,
    tags: ['variant', 'divider', 'separator']
  },

  {
    id: 'variant-disabled',
    title: 'Menu with Disabled Items',
    description: 'Some menu items disabled',
    category: 'variants',
    complexity: 'beginner',
    code: `<InDropdownMenu
  id="menu-disabled"
  :options="options"
  :status="isOpen"
  @select="handleSelect"
/>`,
    props: {
      id: 'menu-disabled',
      status: false
    },
    scriptSetup: `const options = [
  { text: 'Cut', value: 'cut', icon: 'scissors' },
  { text: 'Copy', value: 'copy', icon: 'copy' },
  { text: 'Paste', value: 'paste', icon: 'clipboard', disabled: true },
  { divider: true },
  { text: 'Select All', value: 'select-all', icon: 'check-square' }
];`,
    tags: ['variant', 'disabled', 'states'],
    notes: 'Disabled items are visible but not clickable'
  },

  // ==================== STATES ====================
  {
    id: 'state-searchable',
    title: 'Searchable Menu',
    description: 'Enable search/filter functionality',
    category: 'states',
    complexity: 'intermediate',
    code: `<InDropdownMenu
  id="searchable-menu"
  :options="filteredOptions"
  :status="isOpen"
  :search-status="true"
  @select="handleSelect"
  @search="handleSearch"
/>`,
    props: {
      id: 'searchable-menu',
      status: false,
      searchStatus: true
    },
    scriptSetup: `const isOpen = ref(false);
const searchQuery = ref('');

const allOptions = [
  { text: 'John Doe', value: 'user1', icon: 'user' },
  { text: 'Jane Smith', value: 'user2', icon: 'user' },
  { text: 'Bob Johnson', value: 'user3', icon: 'user' },
  { text: 'Alice Williams', value: 'user4', icon: 'user' }
];

const filteredOptions = computed(() => {
  if (!searchQuery.value) return allOptions;
  return allOptions.filter(option =>
    option.text.toLowerCase().includes(searchQuery.value.toLowerCase())
  );
});

const handleSearch = (query) => {
  searchQuery.value = query;
};

const handleSelect = (value) => {
  console.log('Selected user:', value);
  isOpen.value = false;
  searchQuery.value = '';
};`,
    tags: ['state', 'search', 'filter']
  },

  {
    id: 'state-loading',
    title: 'Loading State',
    description: 'Show loading indicator while fetching data',
    category: 'states',
    complexity: 'intermediate',
    code: `<InDropdownMenu
  id="loading-menu"
  :options="options"
  :status="isOpen"
  :loading-state="isLoading"
  @select="handleSelect"
/>`,
    props: {
      id: 'loading-menu',
      status: false,
      loadingState: true
    },
    scriptSetup: `const isOpen = ref(false);
const isLoading = ref(true);
const options = ref([]);

onMounted(async () => {
  isLoading.value = true;
  try {
    const data = await fetchMenuData();
    options.value = data;
  } finally {
    isLoading.value = false;
  }
});

const handleSelect = (value) => {
  console.log('Selected:', value);
  isOpen.value = false;
};`,
    tags: ['state', 'loading', 'async']
  },

  // ==================== ADVANCED ====================
  {
    id: 'advanced-nested',
    title: 'Nested Submenu',
    description: 'Hierarchical menu with submenus',
    category: 'advanced',
    complexity: 'intermediate',
    code: `<InDropdownMenu
  id="nested-menu"
  :options="options"
  :status="isOpen"
  @select="handleSelect"
/>`,
    props: {
      id: 'nested-menu',
      status: false
    },
    scriptSetup: `const options = [
  { text: 'New', value: 'new', icon: 'plus' },
  {
    text: 'Export',
    value: 'export',
    icon: 'download',
    children: [
      { text: 'Export as PDF', value: 'export-pdf', icon: 'file-pdf' },
      { text: 'Export as CSV', value: 'export-csv', icon: 'file-csv' },
      { text: 'Export as JSON', value: 'export-json', icon: 'file-code' },
      { text: 'Export as Excel', value: 'export-xlsx', icon: 'file-excel' }
    ]
  },
  {
    text: 'Import',
    value: 'import',
    icon: 'upload',
    children: [
      { text: 'Import from CSV', value: 'import-csv' },
      { text: 'Import from JSON', value: 'import-json' }
    ]
  },
  { divider: true },
  { text: 'Print', value: 'print', icon: 'printer' }
];

const handleSelect = (value) => {
  console.log('Action:', value);
  if (value.startsWith('export-')) {
    handleExport(value.replace('export-', ''));
  }
  isOpen.value = false;
};`,
    tags: ['advanced', 'nested', 'submenu']
  },

  {
    id: 'advanced-context-menu',
    title: 'Context Menu',
    description: 'Right-click context menu implementation',
    category: 'advanced',
    complexity: 'advanced',
    code: `<div
  @contextmenu.prevent="handleRightClick"
  class="content-area">
  <!-- Your content -->

  <InDropdownMenu
    id="context-menu"
    :options="contextOptions"
    :status="isContextMenuOpen"
    @select="handleContextAction"
  />
</div>`,
    props: {
      id: 'context-menu',
      status: false
    },
    scriptSetup: `const isContextMenuOpen = ref(false);
const menuPosition = ref({ x: 0, y: 0 });

const contextOptions = [
  { text: 'Cut', value: 'cut', icon: 'scissors' },
  { text: 'Copy', value: 'copy', icon: 'copy' },
  { text: 'Paste', value: 'paste', icon: 'clipboard' },
  { divider: true },
  { text: 'Delete', value: 'delete', icon: 'trash' }
];

const handleRightClick = (event) => {
  menuPosition.value = { x: event.clientX, y: event.clientY };
  isContextMenuOpen.value = true;
};

const handleContextAction = (value) => {
  console.log('Context action:', value);
  isContextMenuOpen.value = false;
};

// Close menu when clicking outside
onMounted(() => {
  document.addEventListener('click', () => {
    isContextMenuOpen.value = false;
  });
});`,
    tags: ['advanced', 'context-menu', 'right-click']
  },

  {
    id: 'advanced-profile-menu',
    title: 'User Profile Menu',
    description: 'Complete user profile dropdown',
    category: 'advanced',
    complexity: 'intermediate',
    code: `<div class="user-dropdown">
  <button class="user-trigger" @click="toggleMenu">
    <img :src="user.avatar" alt="Avatar" />
    <span>{{ user.name }}</span>
    <InIcons name="chevron-down" />
  </button>

  <InDropdownMenu
    id="profile-menu"
    :options="profileOptions"
    :status="isMenuOpen"
    @select="handleProfileAction"
  />
</div>`,
    props: {
      id: 'profile-menu',
      status: false
    },
    scriptSetup: `const isMenuOpen = ref(false);

const user = ref({
  name: 'John Doe',
  email: 'john@example.com',
  avatar: '/avatar.jpg'
});

const profileOptions = [
  { text: 'My Profile', value: 'profile', icon: 'user' },
  { text: 'Account Settings', value: 'settings', icon: 'settings' },
  { text: 'Billing', value: 'billing', icon: 'credit-card' },
  { divider: true },
  { text: 'Help & Support', value: 'help', icon: 'question-circle' },
  { text: 'Documentation', value: 'docs', icon: 'book' },
  { divider: true },
  { text: 'Sign Out', value: 'logout', icon: 'logout' }
];

const toggleMenu = () => {
  isMenuOpen.value = !isMenuOpen.value;
};

const handleProfileAction = (value) => {
  switch (value) {
    case 'profile':
      router.push('/profile');
      break;
    case 'settings':
      router.push('/settings');
      break;
    case 'logout':
      performLogout();
      break;
    default:
      console.log('Action:', value);
  }
  isMenuOpen.value = false;
};`,
    tags: ['advanced', 'profile', 'user-menu']
  },

  {
    id: 'advanced-async-search',
    title: 'Async Search Menu',
    description: 'Search with remote data fetching',
    category: 'advanced',
    complexity: 'advanced',
    code: `<InDropdownMenu
  id="async-search"
  :options="searchResults"
  :status="isOpen"
  :search-status="true"
  :loading-state="isSearching"
  @select="handleSelect"
  @search="handleSearch"
/>`,
    props: {
      id: 'async-search',
      status: false,
      searchStatus: true,
      loadingState: false
    },
    scriptSetup: `const isOpen = ref(false);
const isSearching = ref(false);
const searchResults = ref([]);
let searchTimeout = null;

const handleSearch = async (query) => {
  // Debounce search
  clearTimeout(searchTimeout);

  if (!query) {
    searchResults.value = [];
    return;
  }

  searchTimeout = setTimeout(async () => {
    isSearching.value = true;
    try {
      const results = await api.search(query);
      searchResults.value = results.map(item => ({
        text: item.name,
        value: item.id,
        icon: item.type === 'user' ? 'user' : 'folder'
      }));
    } catch (error) {
      console.error('Search failed:', error);
    } finally {
      isSearching.value = false;
    }
  }, 300);
};

const handleSelect = (value) => {
  console.log('Selected:', value);
  navigateToItem(value);
  isOpen.value = false;
};`,
    tags: ['advanced', 'async', 'search', 'debounce']
  }
];

/**
 * Common mistakes and anti-patterns to avoid
 */
export const antiPatterns = [
  {
    wrong: `<InDropdownMenu :options="options" />`,
    right: `<InDropdownMenu id="menu" :options="options" :status="isOpen" />`,
    reason: 'id and status props are required'
  },
  {
    wrong: `const options = [{ text: 'Item 1' }];`,
    right: `const options = [{ text: 'Item 1', value: 'item1' }];`,
    reason: 'Both text and value are required for menu items'
  },
  {
    wrong: `@select="handleSelect(option)"`,
    right: `@select="handleSelect"`,
    reason: 'Select event automatically passes the value'
  },
  {
    wrong: `<InDropdownMenu :status="true" />`,
    right: `<InDropdownMenu :status="isOpen" @select="() => isOpen = false" />`,
    reason: 'Always control menu state and close it after selection'
  },
  {
    wrong: `{ text: 'Item', value: 'item', icon: 'icon-name-that-doesnt-exist' }`,
    right: `{ text: 'Item', value: 'item', icon: 'valid-icon-name' }`,
    reason: 'Icon names must match InIcons component icon set'
  }
];

/**
 * Best practices for using InDropdownMenu
 */
export const bestPractices = [
  {
    practice: 'Always close menu after selection',
    description: 'Set status to false in select handler',
    example: 'const handleSelect = (value) => { /* action */ isOpen.value = false; }'
  },
  {
    practice: 'Use dividers to group related items',
    description: 'Improve visual organization with separators',
    example: '{ divider: true } between logical groups'
  },
  {
    practice: 'Provide icons for better UX',
    description: 'Icons help users quickly identify menu items',
    example: '{ text: "Settings", value: "settings", icon: "settings" }'
  },
  {
    practice: 'Use descriptive values',
    description: 'Values should be clear and action-oriented',
    example: 'value: "export-pdf" instead of value: "1"'
  },
  {
    practice: 'Handle keyboard navigation',
    description: 'Component supports arrow keys and Enter by default',
    example: 'No additional code needed, built-in support'
  },
  {
    practice: 'Debounce search queries',
    description: 'For async search, debounce to reduce API calls',
    example: 'Use setTimeout with 300ms delay for search'
  }
];

