      showSuccess.value = false;
    }, 5000);
  } catch (error) {
    // Handle error
  }
};

const ribbons = ref([
  { id: 1, text: 'Error message', type: 'error', priority: 1 },
  { id: 2, text: 'Warning message', type: 'warning', priority: 2 },
  { id: 3, text: 'Info message', type: 'default', priority: 3 }
]);

const sortedRibbons = computed(() => {
  return ribbons.value.sort((a, b) => a.priority - b.priority);
});

const closeRibbon = (id) => {
  ribbons.value = ribbons.value.filter(r => r.id !== id);
};

const RIBBON_KEY = 'announcement_v1_dismissed';
const showAnnouncement = ref(!localStorage.getItem(RIBBON_KEY));

const dismissAnnouncement = () => {
  showAnnouncement.value = false;
  localStorage.setItem(RIBBON_KEY, 'true');
};

const formErrors = ref([]);
