      {{ contentError }}
    </span>
  </div>

  <InButtonV2
    type="primary"
    label-text="Submit"
    html-type="submit"
    :disabled-status="!isFormValid"
  />
</form>`,
    props: {
      id: 'content',
      value: ''
    },
    scriptSetup: `const formData = reactive({
  title: '',
  content: ''
});

const toolbar = [
  'heading2',
  'bold',
  'italic',
  'bulletList',
  'orderedList',
  'link'
];

const contentError = ref('');

const isFormValid = computed(() => {
  return formData.title.length > 0 && formData.content.length > 10;
});

const handleSubmit = async () => {
  if (!isFormValid.value) {
    contentError.value = 'Content is required';
    return;
  }

  try {
    await api.submitPost(formData);
    // Success handling
  } catch (error) {
    console.error('Submit failed:', error);
  }
};`,
    tags: ['forms', 'validation', 'submit']
  },

  // ==================== ADVANCED ====================
  {
    id: 'advanced-blog-post',
    title: 'Blog Post Editor',
    description: 'Complete blog post editing interface',
    category: 'advanced',
    complexity: 'advanced',
    code: `<div class="blog-editor">
  <InTextInput
    v-model="post.title"
    placeholder="Post title..."
    class="title-input"
  />

  <InRichTextInput
    id="blog-content"
    v-model="post.content"
    :toolbar="blogToolbar"
    placeholder="Write your blog post..."
  />

  <div class="editor-footer">
    <div class="word-count">
      {{ wordCount }} words
    </div>
    <div class="actions">
      <InButtonV2
        type="secondary"
        label-text="Save Draft"
        @click="saveDraft"
      />
      <InButtonV2
        type="primary"
        label-text="Publish"
        :disabled-status="!canPublish"
        @click="publish"
      />
    </div>
  </div>
</div>`,
    props: {
      id: 'blog-content',
      value: ''
