/**
 * Common mistakes and anti-patterns to avoid
 */
export const antiPatterns = [
  {
    wrong: `<InRichTextInput v-model="content" />`,
    right: `<InRichTextInput id="editor" v-model="content" />`,
    reason: 'id prop is required for proper editor initialization'
  },
  {
    wrong: `<InRichTextInput id="editor" :value="content" />`,
    right: `<InRichTextInput id="editor" v-model="content" />`,
    reason: 'Use v-model for two-way binding with rich text editor'
  },
  {
    wrong: `const content = ref(null);`,
    right: `const content = ref('');`,
    reason: 'Initialize content with empty string, not null'
  },
  {
    wrong: `:toolbar="'bold,italic,underline'"`,
    right: `:toolbar="['bold', 'italic', 'underline']"`,
    reason: 'toolbar prop expects an array, not a string'
  }
];

/**
 * Best practices for using InRichTextInput
 */
export const bestPractices = [
  {
    practice: 'Always use v-model',
    description: 'Two-way binding is essential for editor reactivity',
    example: 'v-model="content" instead of :value and @input'
  },
  {
    practice: 'Customize toolbar for use case',
    description: 'Only show relevant formatting options',
    example: 'Comments: [\'bold\', \'italic\', \'link\']'
  },
  {
    practice: 'Implement auto-save for long content',
    description: 'Prevent data loss with periodic saves',
    example: 'Debounced save on input with 1-2 second delay'
  },
  {
    practice: 'Provide clear placeholder text',
    description: 'Guide users on what to write',
    example: 'placeholder="Write your blog post here..."'
  },
  {
    practice: 'Sanitize HTML output',
    description: 'Always sanitize user-generated HTML before saving',
    example: 'Use DOMPurify or similar library on submit'
  }
];

