    wrong: `text: ''`,
    right: `text: 'Helpful description'`,
    reason: 'Empty tooltips provide no value to users'
  }
];

/**
 * Best practices for using InTooltipV2
 */
export const bestPractices = [
  {
    practice: 'Keep tooltip text concise',
    description: 'Tooltips should be brief and to the point',
    example: 'text: "Save changes" instead of long paragraphs'
  },
  {
    practice: 'Always provide tooltips for icon-only buttons',
    description: 'Essential for accessibility and usability',
    example: 'Icon buttons must have descriptive tooltip text'
  },
  {
    practice: 'Use dynamic positioning near edges',
    description: 'Prevent tooltips from going off-screen',
    example: 'dynamicPosition: true for viewport-aware positioning'
  },
  {
    practice: 'Position tooltips logically',
    description: 'Choose position that does not obscure related content',
    example: 'Use top/bottom for horizontal layouts, left/right for vertical'
  },
  {
    practice: 'Sanitize HTML content',
    description: 'Prevent XSS vulnerabilities in tooltip content',
    example: 'preventXss: true (default) for user-generated content'
  },
  {
    practice: 'Use unique IDs',
    description: 'Ensure each tooltip has a unique identifier',
    example: 'id: \`tooltip-\${item.id}\` for dynamic lists'
  }
];

