import { ref } from 'vue';
import { InButtonV2, TYPES, STYLES, SIZES } from '@useinsider/design-system-vue';
import '@useinsider/design-system-vue/dist/design-system-vue.css';

const isLoading = ref(false);
const showSuccess = ref(false);

const handleSubmit = async () => {
  isLoading.value = true;
  try {
    await api.submit();
    showSuccess.value = true;
    setTimeout(() => {
      showSuccess.value = false;
    }, 2000);
  } catch (error) {
    console.error('Submit failed:', error);
  } finally {
    isLoading.value = false;
  }
};

const handlePeriodChange = (payload) => {
  // payload = { id: 'daily', type: 'secondary', ... }
  console.log('Period changed to:', payload.id);
  currentPeriod.value = payload.id;
};

codeSnippets.style:
/* Button V2 Component Styles */

/* Solid styling (default) */
.in-button-v2.solid {
  border: 1px solid transparent;
}

.in-button-v2.solid.primary {
  background-color: #1976D2;
  color: #FFFFFF;
}

.in-button-v2.solid.danger {
  background-color: #D32F2F;
  color: #FFFFFF;
}

/* Ghost styling */
.in-button-v2.ghost {
  background-color: transparent;
  border: 1px solid currentColor;
}

.in-button-v2.ghost.primary {
  color: #1976D2;
}

.in-button-v2.ghost.primary:hover {
  background-color: #E3F2FD;
}

/* Text styling */
.in-button-v2.text {
  background-color: transparent;
  border: none;
}

.in-button-v2.text.primary {
  color: #1976D2;
}

.in-button-v2.text.primary:hover {
  text-decoration: underline;
}

/* Sizes */
.in-button-v2.default {
  height: 40px;
  padding: 0 16px;
  font-size: 14px;
}

.in-button-v2.small {
  height: 32px;
  padding: 0 12px;
  font-size: 12px;
}

/* States */
.in-button-v2:disabled,
.in-button-v2.loading {
  opacity: 0.5;
  cursor: not-allowed;
}

.in-button-v2.success {
  background-color: #4CAF50;
}

/* Responsive */
@media (max-width: 768px) {
  .in-button-v2 {
    width: 100%; /* Full width on mobile */
  }
}

