const isSubmitting = ref(false);

const handleSubmit = async () => {
  isSubmitting.value = true;
  try {
    await api.submitForm(formData);
    router.push('/success');
  } catch (error) {
    showNotification({ type: 'error', message: error.message });
  } finally {
    isSubmitting.value = false;
  }
};

const showSuccess = ref(false);

const handleSave = async () => {
  await api.save();
  showSuccess.value = true;
  setTimeout(() => {
    showSuccess.value = false;
  }, 2000);
};

const selectedPeriod = ref('daily');

const periodOptions = computed(() => [
  { id: 'daily', type: 'secondary', styling: selectedPeriod.value === 'daily' ? 'solid' : 'ghost', labelText: 'Daily', selectedStatus: selectedPeriod.value === 'daily' },
  { id: 'weekly', type: 'secondary', styling: selectedPeriod.value === 'weekly' ? 'solid' : 'ghost', labelText: 'Weekly', selectedStatus: selectedPeriod.value === 'weekly' },
  { id: 'monthly', type: 'secondary', styling: selectedPeriod.value === 'monthly' ? 'solid' : 'ghost', labelText: 'Monthly', selectedStatus: selectedPeriod.value === 'monthly' }
]);

const handlePeriodChange = (payload) => {
  selectedPeriod.value = payload.id;
};

const confirmDelete = async () => {
  const confirmed = await showConfirmDialog({
    title: 'Delete Item',
    message: 'Are you sure you want to delete this item?',
    confirmText: 'Delete',
    confirmType: 'danger'
  });

  if (confirmed) {
    await deleteItem();
  }
};

