/* Chip uses design system styles */
/* Custom wrapper for chip lists */
.chip-list {
  display: flex;
  flex-wrap: wrap;
  gap: 8px;
  padding: 8px 0;
}

/* Vertical chip list */
.chip-list--vertical {
  flex-direction: column;
  align-items: flex-start;
}

/* Chip container with max items */
.chip-container {
  max-width: 600px;
}

/* Scrollable chip list */
.chip-list--scrollable {
  overflow-x: auto;
  flex-wrap: nowrap;
  padding-bottom: 8px;
}

/* Custom chip spacing */
.chip-list--compact {
  gap: 4px;
}

.chip-list--spacious {
  gap: 12px;
}
