codeSnippets.style:
/* InContainer component base styling */

/* Container wrapper */
.in-container {
  background: #fff;
  display: flex;
  flex-direction: column;
}

/* Header section */
.in-container__header {
  padding: 16px 24px;
  border-bottom: 1px solid #e0e0e0;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

/* Content area */
.in-container__content {
  padding: 24px; /* Only when fullWidthContentStatus=false */
}

.in-container__content--full-width {
  padding: 0; /* When fullWidthContentStatus=true */
}

/* Footer section */
.in-container__footer {
  padding: 16px 24px;
  border-top: 1px solid #e0e0e0;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

/* Color indicator */
.in-container--with-indicator::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 4px;
  background-color: var(--indicator-color);
}

/* Card variant */
.in-container--card {
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
}

/* Border triangle decoration */
.in-container--triangle-top::after {
  content: '';
  position: absolute;
  top: -8px;
  width: 0;
  height: 0;
  border-left: 8px solid transparent;
  border-right: 8px solid transparent;
  border-bottom: 8px solid #fff;
}

