const statusColors = {
  success: '#4CAF50',
  warning: '#FF9800',
  error: '#F44336',
  info: '#2196F3'
};

const statusColor = computed(() => statusColors[props.status] || '');

<InContainer
  :color-indicator-status="!!statusColor"
  :color-indicator-color="statusColor"
  :header-status="true">
  <!-- Content -->
</InContainer>

const formData = ref({ name: '', email: '' });
const errors = ref({});
const isSaving = ref(false);

const validateForm = () => {
  errors.value = {};
  if (!formData.value.name) errors.value.name = 'Name is required';
  if (!formData.value.email) errors.value.email = 'Email is required';
  return Object.keys(errors.value).length === 0;
};

const handleSave = async () => {
  if (!validateForm()) return;

  isSaving.value = true;
  try {
    await api.save(formData.value);
  } finally {
    isSaving.value = false;
  }
};

<InContainer
  :header-status="true"
  :full-width-content-status="false">
  <template #headerTitle>Edit Form</template>
  <template #headerRightSlot>
    <InButtonV2
      type="primary"
      label-text="Save"
      :loading="isSaving"
      @click="handleSave"
    />
  </template>
  <template #contentArea>
    <form><!-- Form fields --></form>
  </template>
</InContainer>

helperFunctions.code:
function createContainerConfig(hasHeader = false, hasFooter = false, options = {}) {
  return {
    headerStatus: hasHeader,
    footerStatus: hasFooter,
    fullWidthContentStatus: false,
    ...options
  };
}

function createCardLayout(title?: string) {
  return {
    headerStatus: !!title,
    footerStatus: false,
    fullWidthContentStatus: false,
    title
  };
}

function createFormContainer(withFooterActions = true) {
  return {
    headerStatus: true,
    footerStatus: withFooterActions,
    fullWidthContentStatus: false
  };
}
