  align-items: center;
  padding: 0 12px;
}

/* Pinned column styling */
.ag-pinned-left-header,
.ag-pinned-left-cols-container {
  border-right: 2px solid #e0e0e0;
}

.ag-pinned-right-header,
.ag-pinned-right-cols-container {
  border-left: 2px solid #e0e0e0;
