const searchParams = ref({
  teamId: currentTeam.value.id,
  status: selectedStatus.value
});

const handlePaginationChange = async ({ page, perPage }) => {
  // Table automatically fetches with updated params
  console.log('Fetching page', page);
};

// Update params reactively
watch([currentTeam, selectedStatus], () => {
  searchParams.value = {
    teamId: currentTeam.value.id,
    status: selectedStatus.value
  };
});

const tasks = ref([...]);

const handleRowReorder = (event) => {
  const { node, overNode } = event;

  // Update local data
  const movedItem = node.data;
  const targetIndex = overNode.rowIndex;

  const newData = [...tasks.value];
  const oldIndex = newData.findIndex(item => item.id === movedItem.id);

  newData.splice(oldIndex, 1);
  newData.splice(targetIndex, 0, movedItem);

  tasks.value = newData;
};

const saveDraggedOrder = async () => {
  // Save new order to backend
  await api.updateTaskOrder(tasks.value.map((t, i) => ({
    id: t.id,
    order: i
  })));
};

const selectedRows = ref([]);

const handleSelection = (event) => {
  selectedRows.value = event.selectedRows;
};

const handleBulkAction = async ({ actionId, selectedRows }) => {
  if (actionId === 'delete') {
    const confirmed = await showConfirmDialog({
      title: 'Delete Items',
      message: `Delete ${selectedRows.length} items?`
    });

    if (confirmed) {
      await api.bulkDelete(selectedRows.map(r => r.id));
      // Refresh table
    }
  } else if (actionId === 'export') {
    exportToCSV(selectedRows);
  }
};

const columns = [
  {
    field: 'status',
    headerName: 'Status',
    cellRenderer: 'InToggle',
    cellRendererParams: {
      onToggle: async (rowData, newValue) => {
        await api.updateStatus(rowData.id, newValue);
        // Update local data
        const row = data.value.find(r => r.id === rowData.id);
        if (row) row.status = newValue;
      }
    }
  }
];
