const searchParams = ref({
  teamId: currentTeam.value.id,
  status: selectedStatus.value
});

const handlePaginationChange = async ({ page, perPage }) => {
  // Table automatically fetches with updated params
  console.log('Fetching page', page);
};

// Update params reactively
watch([currentTeam, selectedStatus], () => {
  searchParams.value = {
    teamId: currentTeam.value.id,
    status: selectedStatus.value
  };
});

const tasks = ref([...]);

const handleRowReorder = (event) => {
  const { node, overNode } = event;

  // Update local data
  const movedItem = node.data;
  const targetIndex = overNode.rowIndex;

  const newData = [...tasks.value];
  const oldIndex = newData.findIndex(item => item.id === movedItem.id);

  newData.splice(oldIndex, 1);
  newData.splice(targetIndex, 0, movedItem);

  tasks.value = newData;
};

const saveDraggedOrder = async () => {
  // Save new order to backend
  await api.updateTaskOrder(tasks.value.map((t, i) => ({
    id: t.id,
    order: i
  })));
};

const selectedRows = ref([]);

const handleSelection = (event) => {
  selectedRows.value = event.selectedRows;
};

const handleBulkAction = async ({ actionId, selectedRows }) => {
  if (actionId === 'delete') {
    const confirmed = await showConfirmDialog({
      title: 'Delete Items',
      message: `Delete ${selectedRows.length} items?`
    });

    if (confirmed) {
      await api.bulkDelete(selectedRows.map(r => r.id));
      // Refresh table
    }
  } else if (actionId === 'export') {
    exportToCSV(selectedRows);
  }
};

const columns = [
  {
    field: 'status',
    headerName: 'Status',
    cellRenderer: 'InToggle',
    cellRendererParams: {
      onToggle: async (rowData, newValue) => {
        await api.updateStatus(rowData.id, newValue);
        // Update local data
        const row = data.value.find(r => r.id === rowData.id);
        if (row) row.status = newValue;
      }
    }
  }
];

helperFunctions.code:
function createColumnDef(field: string, headerName: string, options = {}) {
  return {
    field,
    headerName,
    sortable: true,
    filter: true,
    resizable: true,
    ...options
  };
}

function formatCurrency(params: any) {
  if (params.value == null) return '';
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD'
  }).format(params.value);
}

function formatDate(params: any, format = 'MMM DD, YYYY') {
  if (!params.value) return '';
  const date = new Date(params.value);
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}

function getSelectedRowIds(selectedRows: any[], idField = 'id') {
  return selectedRows.map(row => row[idField]);
}

function createServerSideParams(page: number, perPage: number, sortBy?: string, sortOrder?: string, filters = {}) {
  return {
    page,
    perPage,
    sortBy: sortBy || 'createdAt',
    sortOrder: sortOrder || 'desc',
    ...filters
  };
}

function bulkUpdateRowData(data: any[], updates: Array<{ id: any, changes: object }>, idField = 'id') {
  const updateMap = new Map(updates.map(u => [u.id, u.changes]));

  return data.map(row => {
    const changes = updateMap.get(row[idField]);
    return changes ? { ...row, ...changes } : row;
  });
}

function exportToCSV(data: any[], filename = 'export.csv', columns?: string[]) {
  const keys = columns || Object.keys(data[0] || {});
  const csv = [
    keys.join(','),
    ...data.map(row => keys.map(key => JSON.stringify(row[key] || '')).join(','))
  ].join('\n');

  const blob = new Blob([csv], { type: 'text/csv' });
  const url = URL.createObjectURL(blob);
  const link = document.createElement('a');
  link.href = url;
  link.download = filename;
  link.click();
  URL.revokeObjectURL(url);
}
