helperFunctions.code:
export function useDrawerManager(options = {}) {
  const { onOpen, onClose, resetOnClose = true } = options;

  const isOpen = ref(false);
  const data = ref(null);

  const open = (initialData) => {
    data.value = initialData || {};
    isOpen.value = true;
    onOpen?.();
  };

  const close = () => {
    isOpen.value = false;
    if (resetOnClose) {
      setTimeout(() => {
        data.value = null;
      }, 300); // Wait for close animation
    }
    onClose?.();
  };

  const toggle = () => {
    if (isOpen.value) {
      close();
    } else {
      open();
    }
  };

  return {
    isOpen,
    data,
    open,
    close,
    toggle
  };
}

helperFunctions.example:
const editDrawer = useDrawerManager({
  onClose: () => console.log('Drawer closed'),
  resetOnClose: true
});

// Open with data
editDrawer.open({ id: 1, name: 'Campaign' });

// In template:
<InDrawer
  v-model:status="editDrawer.isOpen.value"
  @onCloseEvent="editDrawer.close"
/>

helperFunctions.code:
export function createWizardSteps(steps) {
  const currentStepIndex = ref(0);

  const currentStep = computed(() => steps[currentStepIndex.value]);
  const isFirstStep = computed(() => currentStepIndex.value === 0);
  const isLastStep = computed(() => currentStepIndex.value === steps.length - 1);
  const progress = computed(() => ((currentStepIndex.value + 1) / steps.length) * 100);

  const nextStep = () => {
    if (!isLastStep.value) {
      currentStepIndex.value++;
    }
  };

  const prevStep = () => {
    if (!isFirstStep.value) {
      currentStepIndex.value--;
    }
  };

  const goToStep = (index) => {
    if (index >= 0 && index < steps.length) {
      currentStepIndex.value = index;
    }
  };

  const reset = () => {
    currentStepIndex.value = 0;
  };

  const footerButtons = computed(() => ({
    primaryButton: {
      text: isLastStep.value ? 'Finish' : 'Next',
      onClick: nextStep
    },
    secondaryButton: {
      text: isFirstStep.value ? 'Cancel' : 'Back',
      onClick: prevStep
    }
  }));

  return {
    currentStepIndex,
    currentStep,
    isFirstStep,
    isLastStep,
    progress,
    nextStep,
    prevStep,
    goToStep,
    reset,
    footerButtons
  };
}

helperFunctions.example:
const steps = [
  { title: 'Basic Info', component: BasicInfoStep },
  { title: 'Details', component: DetailsStep },
  { title: 'Review', component: ReviewStep }
];

const wizard = createWizardSteps(steps);

// In template:
<InDrawer
  :titleText="wizard.currentStep.value.title"
  :footerButtonGroupOptions="wizard.footerButtons.value"
/>
