import { ref, computed, watch } from 'vue';

interface DynamicContent {
  selectedEvents?: string[];
  selectedAttributes?: string[];
  selectedPartners?: string[];
  groupedEvents?: string[];
}

export function useDynamicContentManager(
  initialContent: DynamicContent = {},
  options: {
    onSave?: (content: DynamicContent) => Promise<void>;
    onLoad?: () => Promise<DynamicContent>;
    validateBeforeSave?: boolean;
  } = {}
) {
  const content = ref<DynamicContent>({ ...initialContent });
  const originalContent = ref<DynamicContent>({ ...initialContent });
  const saving = ref(false);
  const loading = ref(false);
  const error = ref<string | null>(null);

  const isDirty = computed(() => {
    return JSON.stringify(content.value) !== JSON.stringify(originalContent.value);
  });

  const hasSelections = computed(() => {
    return (
      (content.value.selectedEvents?.length || 0) > 0 ||
      (content.value.selectedAttributes?.length || 0) > 0 ||
      (content.value.selectedPartners?.length || 0) > 0
    );
  });

  const validate = (): { valid: boolean; errors: string[] } => {
    const errors: string[] = [];

    // Example validations
    if (!content.value.selectedEvents || content.value.selectedEvents.length === 0) {
      errors.push('At least one event must be selected');
    }

    if (!content.value.selectedPartners || content.value.selectedPartners.length === 0) {
      errors.push('At least one partner must be selected');
    }

    return {
      valid: errors.length === 0,
      errors
    };
  };

  const save = async () => {
    if (options.validateBeforeSave) {
      const validation = validate();
      if (!validation.valid) {
        error.value = validation.errors.join(', ');
        return false;
      }
    }

    if (!options.onSave) {
      console.warn('No onSave handler provided');
      return false;
    }

    saving.value = true;
    error.value = null;

    try {
      await options.onSave(content.value);
      originalContent.value = { ...content.value };
      return true;
    } catch (err: any) {
      error.value = err.message || 'Save failed';
      return false;
    } finally {
      saving.value = false;
    }
  };

  const load = async () => {
    if (!options.onLoad) {
      console.warn('No onLoad handler provided');
      return;
    }

    loading.value = true;
    error.value = null;

    try {
      const loadedContent = await options.onLoad();
      content.value = { ...loadedContent };
      originalContent.value = { ...loadedContent };
    } catch (err: any) {
      error.value = err.message || 'Load failed';
    } finally {
      loading.value = false;
    }
  };

  const reset = () => {
    content.value = { ...originalContent.value };
    error.value = null;
  };

  const clear = () => {
    content.value = {
      selectedEvents: [],
      selectedAttributes: [],
      selectedPartners: [],
      groupedEvents: []
    };
  };

  return {
    content,
    isDirty,
    hasSelections,
    saving,
    loading,
    error,
    validate,
    save,
    load,
    reset,
    clear
  };
}

