import { useDynamicContentManager } from './composables/useDynamicContentManager';

const manager = useDynamicContentManager(
  { selectedEvents: [], selectedAttributes: [] },
  {
    onSave: async (content) => {
      await api.saveAnalyticsConfig(content);
    },
    onLoad: async () => {
      return await api.loadAnalyticsConfig();
    },
    validateBeforeSave: true
  }
);

