<InDynamicContentBox
  v-model:dynamicContent="manager.content.value"
  :buttonOptions="[
    {
      label: 'Save',
      variant: 'primary',
      disabled: !manager.isDirty.value || manager.saving.value,
      callback: manager.save
    },
    {
      label: 'Reset',
      variant: 'secondary',
      callback: manager.reset
    }
  ]"
