select.handlingPattern:
@select="handleSelect"

handleSelect(option) {
  const isSelected = this.value.some(v => v.value === option.value);
  if (isSelected) {
    // Remove from selection
    this.value = this.value.filter(v => v.value !== option.value);
  } else {
    // Add to selection
    this.value = [...this.value, option];
  }
}

helperFunctions.code:
function transformToOptions(items: string[]) {
  return items.map(item => ({
    text: item,
    value: item.toLowerCase().replace(/\s+/g, '-')
  }));
}

function getSelectedValues(selected: Array<{ text: string, value: any }>) {
  return selected.map(item => item.value);
}

helperFunctions.example:
const values = getSelectedValues(selected);
// If selected = [{ text: 'Turkey', value: 'TR' }]
// Returns: ['TR']

helperFunctions.code:
function isMaxSelectionReached(selected: any[], max: number | undefined) {
  if (!max) return false;
  return selected.length >= max;
}

accessibilityNotes.example:
// Automatically supported:
// Tab - Focus dropdown
// Arrow Up/Down - Navigate options
// Space - Toggle selection
// Backspace - Remove last tag
// Escape - Close dropdown
