helperFunctions.code:
export function useEmptyState(data, options = {}) {
  const { canCreate = true, hasFilters = ref(false) } = options;

  const isEmpty = computed(() => data.value.length === 0);

  const emptyStateConfig = computed(() => {
    if (!isEmpty.value) return null;

    if (hasFilters.value) {
      return {
        title: 'No matches found',
        description: 'Try adjusting your filters or search criteria',
        icon: 'filter',
        buttonStatus: true,
        buttonText: 'Clear Filters'
      };
    }

    return {
      title: options.emptyTitle || 'No items yet',
      description: options.emptyDescription || 'Create your first item to get started',
      icon: options.emptyIcon || 'inbox-empty',
      buttonStatus: canCreate,
      buttonText: options.createText || 'Create Item'
    };
  });

  return {
    isEmpty,
    emptyStateConfig
  };
}

helperFunctions.example:
const campaigns = ref([]);
const hasFilters = ref(false);

const { isEmpty, emptyStateConfig } = useEmptyState(campaigns, {
  hasFilters,
  canCreate: hasPermission('create'),
  emptyTitle: 'No campaigns',
  emptyDescription: 'Create your first campaign',
  createText: 'Create Campaign'
});

// In template:
<InNoData v-if="isEmpty" v-bind="emptyStateConfig" />
