export function useProgressTracker(totalItems = 100) {
  const progress = ref(0);
  const isSuccess = ref(false);
  const isError = ref(false);
  const errorMessage = ref('');
  const isProcessing = ref(false);

  const start = () => {
    progress.value = 0;
    isSuccess.value = false;
    isError.value = false;
    errorMessage.value = '';
    isProcessing.value = true;
  };

  const increment = (amount = 1) => {
    progress.value = Math.min(progress.value + amount, totalItems);
  };

  const setProgress = (value) => {
    progress.value = Math.min(value, totalItems);
  };

  const complete = () => {
    progress.value = totalItems;
    isSuccess.value = true;
    isProcessing.value = false;
  };

  const fail = (message) => {
    isError.value = true;
    errorMessage.value = message;
    isProcessing.value = false;
  };

  const reset = () => {
    start();
    isProcessing.value = false;
  };

  const percentage = computed(() => 
    Math.round((progress.value / totalItems) * 100)
  );

  return {
    progress,
    isSuccess,
    isError,
    errorMessage,
    isProcessing,
    percentage,
    start,
    increment,
    setProgress,
    complete,
    fail,
    reset
  };
}

