export async function createProgressFromPromise(promises, onProgress) {
  const total = promises.length;
  let completed = 0;
  let errors = [];

  const results = await Promise.allSettled(
    promises.map(async (promise, index) => {
      try {
        const result = await promise;
        completed++;
        onProgress({
          progress: completed,
          total,
          percentage: Math.round((completed / total) * 100),
          current: index,
          isComplete: completed === total,
          hasErrors: errors.length > 0
        });
        return result;
      } catch (error) {
        errors.push({ index, error });
        completed++;
        onProgress({
          progress: completed,
          total,
          percentage: Math.round((completed / total) * 100),
          current: index,
          isComplete: completed === total,
          hasErrors: true,
          errors
        });
        throw error;
      }
    })
  );

  return {
    results,
    errors,
    succeeded: results.filter(r => r.status === 'fulfilled').length,
    failed: results.filter(r => r.status === 'rejected').length
  };
}

helperFunctions.example:
const files = ['file1.txt', 'file2.txt', 'file3.txt'];
const uploadPromises = files.map(file => uploadFile(file));

const { progress } = useProgressTracker(files.length);

await createProgressFromPromise(uploadPromises, (state) => {
  progress.value = state.progress;
  if (state.isComplete) {
    if (state.hasErrors) {
      console.log(`Completed with ${state.errors.length} errors`);
    } else {
      console.log('All uploads successful!');
    }
  }
});
