:maxWidth="150"

bestPractices.code:
// Good:
<InTagsText
  :disable="!canEditRoles"
  @click="manageRole"
/>

// Bad:
<InTagsText @click="maybeManageRole" />
// then check permission inside handler

commonMistakes.example:
// Wrong:
<InTagsText text="Active" value="tag" />
<InTagsText text="Paused" value="tag" /> // duplicate!

// Correct:
<InTagsText text="Active" value="active" />
<InTagsText text="Paused" value="paused" />

// Wrong:
// Page 1: Active = success
// Page 2: Active = primary

// Correct: Use same mapping everywhere
const STATUS_TYPES = {
  active: 'success',
  paused: 'warning'
};

// Wrong (looks clickable but isn't):
<InTagsText text="Status" value="id" /> // no @click handler

// Correct:
<InTagsText text="Status" value="id" :disable="true" />

accessibilityNotes.example:
// Good: Color + Text
<InTagsText text="Failed" type="danger" />

// Bad: Color only (no text)
<InTagsText text="" type="danger" />

helperFunctions.code:
export function createStatusTagConfig(status: string) {
  const configs = {
    active: { text: 'Active', type: 'success' },
    paused: { text: 'Paused', type: 'warning' },
    scheduled: { text: 'Scheduled', type: 'info' },
    failed: { text: 'Failed', type: 'danger' },
    draft: { text: 'Draft', type: 'default' },
    completed: { text: 'Completed', type: 'success' },
    cancelled: { text: 'Cancelled', type: 'default' },
    pending: { text: 'Pending', type: 'warning' }
  };

  return configs[status] || { text: status, type: 'default' };
}

helperFunctions.example:
const statusConfig = createStatusTagConfig('active');
// Returns: { text: 'Active', type: 'success' }

<InTagsText
  v-bind="statusConfig"
  :value="campaign.status"
/>

helperFunctions.code:
export function useTagFilters(items, options = {}) {
  const { filterKey = 'category', allValue = 'all' } = options;
  const activeFilter = ref(allValue);

  const filteredItems = computed(() => {
    if (activeFilter.value === allValue) {
      return items.value;
    }
    return items.value.filter(item => 
      item[filterKey] === activeFilter.value
    );
  });

  const setFilter = (tag) => {
    activeFilter.value = tag.value;
  };

  const clearFilter = () => {
    activeFilter.value = allValue;
  };

  const isActive = (value) => {
    return activeFilter.value === value;
  };

  return {
    activeFilter,
    filteredItems,
    setFilter,
    clearFilter,
    isActive
  };
}

helperFunctions.example:
const campaigns = ref([...]);
const categories = ref([
  { text: 'All', value: 'all' },
  { text: 'Email', value: 'email' },
  { text: 'SMS', value: 'sms' }
]);

const { filteredItems, setFilter, isActive } = useTagFilters(campaigns, {
  filterKey: 'type'
});

// In template:
<InTagsText
  v-for="cat in categories"
  :key="cat.value"
  :text="cat.text"
  :value="cat.value"
  :type="isActive(cat.value) ? 'primary' : 'default'"
  @click="setFilter"
/>
