export function createUndoToast(toastManager, config) {
  const { 
    message, 
    onUndo, 
    onCommit, 
    undoText = 'Undo',
    duration = 5000 
  } = config;

  let committed = false;

  // Show toast with undo button
  const { dismiss } = toastManager.show({
    text: message,
    type: 'success',
    duration,
    actionButtonsConfig: [
      {
        text: undoText,
        onClick: () => {
          onUndo();
          dismiss();
          committed = true;
        }
      }
    ]
  });

  // Commit after duration if not undone
  setTimeout(() => {
    if (!committed) {
      onCommit();
    }
  }, duration);
}

helperFunctions.example:
const toast = useToastManager();

const deleteItem = (item) => {
  // Optimistic UI update
  items.value = items.value.filter(i => i.id !== item.id);

  createUndoToast(toast, {
    message: `${item.name} deleted`,
    onUndo: () => {
      // Restore item
      items.value.push(item);
    },
    onCommit: () => {
      // Permanently delete
      api.delete(item.id);
    },
    duration: 5000
  });
};
