function createTooltipConfig(text: string, position = 'top center', options = {}) {
  return {
    tooltipText: text,
    staticPosition: position,
    dynamicPosition: true,
    offset: { x: 0, y: 8 },
    preventXss: true,
    ...options
  };
}

function truncateWithTooltip(text: string, maxLength: number) {
  const isTruncated = text.length > maxLength;
  return {
    displayText: isTruncated ? text.slice(0, maxLength) + '...' : text,
    tooltipText: isTruncated ? text : null,
    needsTooltip: isTruncated
  };
}
